import Editora from './Editora';


class ControleEditora{
    editoras: [];
    constructor(editoras:[]){
        this.editoras = editoras;
    }
    getNomeEditora(codEditora: number){
        return this.editoras.find(editoras => Editora.codEditora === this.codEditora);
    }
}

interface Editora{
    codEditora: number;
    nome:string;
  }

const editoras: Editora[] = [
    {codEditora: 1, nome: "Editora A"}, 
    {codEditora: 2, nome: "Editora C"},
    {codEditora: 3, nome: "Editora B"},
  ];

const editorasJSON: string = JSON.stringify(editoras);
console.log(editoras.find(editora => editora.codEditora === 1));






export {};
