import React, { useState } from "react";
import { ControleLivro } from "./controle/ControleLivros";
import { ControleEditora, editoras } from "./controle/ControleEditora";
import { useNavigate } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";

const LivroDados = () => {
  const navigate = useNavigate();

  const [state, setState] = useState({
    titulo: "",
    resumo: "",
    autores: "",
    codEditora: editoras.length === 0 ? 0 : editoras.length + 1,
  });

  const controleLivro = new ControleLivro();
  const controleEditora = new ControleEditora(editoras);
  const opcoes = controleEditora.editoras.map((editora) => ({
                                        value: editora.codEditora,
                                        text: editora.nome,
                                      }));

  const handleSubmit = (event) => {
    event.preventDefault();
    const { titulo, resumo, autores, codEditora } = state;
    const novoLivro = {
        codEditora: codEditora,
        codigo: "",
        resumo: resumo,
        titulo: titulo,
        autores: autores.split("\n"),
    };
    controleLivro
      .incluirLivro(novoLivro)
      .then(() => {
        navigate("/");
      })
      .catch((error) => {
        console.error("Erro ao incluir livro:", error);
      });
  };

  const handleChange = (event) => {
    const { name, value } = event.target;
    setState({ ...state, [name]: value });
  };

  return (
    <main className="mt-2 px-5">
      <h1>Dados do Livro</h1>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="titulo">
          <Form.Label>TÃ­tulo</Form.Label>
          <Form.Control
            type="text"
            style={{ width: "500pt" }}
            value={state.titulo}
            onChange={handleChange}
            name="titulo"
          />
        </Form.Group>
        <Form.Group controlId="resumo">
          <Form.Label>Resumo</Form.Label>
          <Form.Control
            as="textarea"
            style={{ height: "100px", width: "500pt", resize: "none" }}

            value={state.resumo}
            onChange={handleChange}
            name="resumo"
          />
        </Form.Group>
        <Form.Group controlId="codEditora">
          <Form.Label>Editora</Form.Label>
          <Form.Control
              as="select"
              onChange={handleChange}
              style={{ width: "500pt" }}

              name="codEditora"
          >
            {opcoes.map((opcao) => (
              <option key={opcao.value} value={opcao.value}>
                {opcao.text}
              </option>
            ))}
          </Form.Control>
        </Form.Group>
        <Form.Group controlId="autores">
          <Form.Label>Autores (1 por linha)</Form.Label>
          <Form.Control
              onChange={handleChange}

              style={{ height: "100px", width: "500pt", resize: "none" }}
              value={state.autores}
              as="textarea"
              name="autores"
          />
        </Form.Group>
        <Button type="submit">Salvar Dados</Button>
      </Form>
    </main>
  );
};

export { LivroDados };
