import React, { useState, useEffect } from 'react';
import { ControleLivro } from './controle/ControleLivros';
import { ControleEditora, editoras } from './controle/ControleEditora';
import { Button, Table } from 'react-bootstrap';

function LivroLista(props) {
    const controleLivro = new ControleLivro();
    const controleEditora = new ControleEditora(editoras);
    const livroDao = require("../modelo/livro-dao");

    const [livros, setLivros] = useState([]);
    const [carregado, setCarregado] = useState(false);

    useEffect(() => {
        const carregarLivros = async () => {
            try {
                const livrosCarregados = await controleLivro.obterLivros();
                setLivros(livrosCarregados);
                setCarregado(true);
            } catch (error) {
                console.error("Erro ao carregar livros:", error);
                setCarregado(true);
            }
        };

        if (!carregado) {
            carregarLivros();
        }
    }, [carregado]);

    const excluir = async (codigoLivro) => {
        try {
            await controleLivro.excluir(codigoLivro);
            setCarregado(false);
        } catch (error) {
            console.error("Erro ao excluir livro:", error);
        }

    };

    const LinhaLivro = ({ livro }) => {
        const nomeEditora = controleEditora.getNomeEditora(livro.codEditora);

        return (
            <tr>
                <td>{livro.titulo} <br/>
                <Button variant="danger" key={livro.codigo} onClick={() => excluir(livro.codigo)}>Excluir </Button>
                </td>



                <td>{livro.resumo}</td>
                <td>{nomeEditora}</td>
                <td>
                    <ul>
                        {livro.autores.map((autor, index) => (
                            <li key={index}>{autor}</li>
                        ))}
                    </ul>
                </td>
            </tr>
        );
    };

    return (
        <div>
            <main className='mt-2 px-5'>
                <h1>Catálogo de Livros </h1>
                {carregado ? (
                    <Table color="white" striped="columns" size="sm">
                        <thead>
                            <tr bgcolor='black' style={{ color: "white" }} >
                                <th >Título</th>
                                <th >Resumo</th>
                                <th>Editora</th>
                                <th>Autores</th>
                            </tr>
                        </thead>
                        <tbody>
                            {livros.length > 0 ? (
                                livros.map((livro) => (
                                    <LinhaLivro key={livro.codigo} livro={livro} />
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="4">Não há nenhum livro</td>
                                </tr>
                            )}
                        </tbody>
                    </Table>
                ) : (
                    <h1>Carregando...</h1>
                )}
            </main>
        </div>
    );
}

export { LivroLista };
