import React, { useState, useEffect } from 'react';
import { ControleLivro, lista_livros } from './controle/ControleLivros';
import { ControleEditora, editoras } from './controle/ControleEditora';

function LivroLista(props) {
    const controleLivro = new ControleLivro(lista_livros);
    const controleEditora = new ControleEditora(editoras); 

    const LinhaLivro = (props) => {
        const { livro, excluir } = props;

        // Obter o nome da editora
        const nomeEditora = livro.nomeEditora; // Supondo que livro já tenha o atributo nomeEditora definido

        return React.createElement('tr', null,
            React.createElement('td', null,
                React.createElement('button', { onClick: () => excluir(livro.codigo) }, 'Excluir')
            ),
            React.createElement('td', null, livro.titulo),
            React.createElement('td', null, livro.autor.join(", ")),
            React.createElement('td', null, nomeEditora)
        );
    };

    return (
        <div>
            <LinhaLivro/>
        </div>
  );
}


export default LivroLista;
