import {React, useState} from "react";
import { ControleLivro, lista_livros, Livro } from "./controle/ControleLivros";
import { ControleEditora, editoras, Editora} from "./controle/ControleEditora";
import {useNavigate} from "react-router-dom";

const LivroDados = () => {
    const navigate = useNavigate();

    const controleLivro = new ControleLivro(lista_livros);
    const controleEditora = new ControleEditora(editoras);
    const opcoes = controleEditora.getEditoras().map(editora => ({value: editora.codEditora, text: editora.nome}));


    const [titulo, setTitulo] = useState(""); 
    const [resumo, setResumo] = useState("");
    const [autores, setAutores] = useState("");
    const tam = controleEditora.editoras.length;
    const [codEditora, setCodEditora] = useState(tam === 0? 0: tam+1);

    console.log(opcoes);

    const incluir = (evento) =>{
        evento.preventDefault();
        const novoLivro = [{
            codigo: 0,
            codEditora: codEditora,
            titulo: titulo,
            resumo: resumo,
            autores: autores.split('\n')
        }]; 
        controleLivro.incluirLivro(novoLivro);
        console.log('x', controleLivro.obterLivros());
        navigate('/');
    }
    const tratarCombo = (evento) => {
        const valorSelecionado: number = parseInt(evento.target.value, 10);
        setCodEditora(valorSelecionado);
    }   

    return(
        <main>
            <h1>Dados do Livro</h1>
            <form onSubmit={incluir}>
                <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} placeholder="TÃ­tulo" />
                <textarea value={resumo} onChange={(e) => setResumo(e.target.value)} placeholder="Resumo"></textarea>
                <textarea value={autores} onChange={(e) => setAutores(e.target.value)} placeholder="Autores (separe por linha)"></textarea>

                <select value={codEditora} onChange={tratarCombo}>
                    {opcoes.map(opcao => (
                        <option key={opcao.value} value={opcao.value}>{opcao.text}</option>
                    ))}
                </select>



                <button type="submit">Incluir Livro</button>
            </form>
        </main>
    );
}; 

export {LivroDados};
