import {React, useState} from "react";
import { ControleLivro, lista_livros } from "./controle/ControleLivros";
import { ControleEditora, editoras, Editora} from "./controle/ControleEditora";
import {useNavigate} from "react-router-dom";

const LivroDados = () => {
    const navigate = useNavigate();

    const controleLivro = new ControleLivro(editoras);
    const controleEditora = new ControleEditora(lista_livros);
    const opcoes = controleEditora.getEditoras().map(editora => ({value: editora.codEditora, text: editora.nome}));


    const [titulo, setTitulo] = useState(""); 
    const [resumo, setResumo] = useState("");
    const [autores, setAutores] = useState("");
    const tam = controleEditora.editoras.length;
    const [codEditora, setCodEditora] = useState(tam === 0? 0: tam+1);
    console.log(opcoes);
    const incluir = (evento) =>{
        evento.preventDefault();
        const novoLivro = {
            codigo: 0, 
            titulo: titulo,
            resumo: resumo,
            autores: autores.split(','),
            codEditora: codEditora
        }; 
        controleLivro.incluir(novoLivro);
        navigate('/');
    }
    const tratarCombo = (evento) =>{
        const valor = parseInt(evento.target.value, 10); 
        setCodEditora(valor);
        return(
            <div>
                <select onChange={tratarCombo}>
                    <option value="1">Editora 1</option>
                    <option value="2">Editora 2</option>
                    {/* Adicione mais opções conforme necessário */}
                </select>
                <p>Cod Editora Selecionada: {codEditora}</p>
            </div>
        );
    }
    return(
        <main>
            <form onSubmit={incluir}>
                <input type="text" value={titulo} onChange={(e) => setTitulo(e.target.value)} placeholder="Título" />
                <textarea value={resumo} onChange={(e) => setResumo(e.target.value)} placeholder="Resumo"></textarea>
                <textarea value={autores} onChange={(e) => setAutores(e.target.value)} placeholder="Autores (separe por linha)"></textarea>
                <input type="number" value={codEditora} onChange={(e) => setCodEditora(parseInt(e.target.value))} placeholder="Código da Editora" />
                <button type="submit">Incluir Livro</button>
            </form>
        </main>
    );
}; 

export {LivroDados};
