import React, { useState, useEffect } from 'react';
import {ControleEditora} from './controle/ControleEditora';
import {ControleLivro, lista_livros, Livro} from './controle/ControleLivros';
// Importe os controladores de livros e editoras, se necessário
// Importe o componente LinhaLivro, se necessário

const LivroLista = () => {
    // Instanciar um controlador de livros e um controlador de editoras
    const controleLivro = new ControleLivro(lista_livros);
    const controleEditora = new ControleEditora();
    // Definir as propriedades livros e carregado com useState
    const [livros, setLivros] = useState([controleLivro.obterLivros(), controleLivro.incluirLivro]);
    const [carregado, setCarregado] = useState(false);

// Utilizar o Hook useEffect para carregar os livros

    // Componente auxiliar LinhaLivro
    const LinhaLivro = (props) => {
        const { livro, excluir } = props;

        return (
          <div className="linha-livro">
            <span>{livro.titulo}</span>
            <span>{livro.autores}</span>
            <button onClick={() => excluir(livro.id)}>Excluir</button>
          </div>
        );
      };

    return (
        <main>
            <h1>Lista de Livros</h1>
            {livros.length > 0 ? (
                <table>
                    <thead>
                        <tr>
                            <th>Excluir</th>
                            <th>Título</th>
                            <th>Autores</th>
                            <th>Editora</th>
                        </tr>
                    </thead>
                    <tbody>
                        {livros.map((livro, index) => (
                            <LinhaLivro key={livro.codigo} livro={livro} excluir={excluir} />
                        ))}
                    </tbody>
                </table>
            ) : (
                <p>Nenhum livro encontrado.</p>
            )}
        </main>
    );
};

export default LivroLista;
