import React, { useState, useEffect } from 'react';
import { ControleLivro, lista_livros } from './controle/ControleLivros';

const LivroLista = () => {
    // Definir o estado inicial dos livros como um array vazio
    const [livros, setLivros] = useState([]);
    const [carregado, setCarregado] = useState(false);


    const LinhaLivro = ({ livro, excluir }) => {
    return (
        <tr>
        <td>
            <button onClick={() => excluir(livro.codigo)}>Excluir</button>
        </td>
        <td>{livro.titulo}</td>
        <td>{livro.autor}</td>
        {/* Adicione mais colunas para outras informações do livro, se necessário */}
        </tr>
        );}

    // Utilizar o Hook useEffect para carregar os livros
    useEffect(() => {
        // Simulando uma operação assíncrona de carregamento de livros
        setTimeout(() => {
            const controleLivro = new ControleLivro(lista_livros);
            setLivros(controleLivro.livros);
            setCarregado(true);
        }, 2000); // Tempo simulado de 1 segundo
    }, []);

    return (
        <main>
            <h1>Lista de Livros</h1>
            {carregado ? (
                livros.length > 0 ? (
                    <table>
                        <thead>
                            <tr>
                                <th>Excluir</th>
                                <th>Título</th>
                                <th>Autor</th>
                                {/* Se necessário, adicione o cabeçalho para Editora */}
                            </tr>
                        </thead>
                        <tbody>
                            {livros.map((livro, index) => (
                                <LinhaLivro key={livro.codigo} livro={livro} />
                            ))}
                        </tbody>
                    </table>
                ) : (
                    <p>Nenhum livro encontrado.</p>
                )
            ) : (
                <p>Carregando livros...</p>
            )}
        </main>
    );
};

export default LivroLista;
