import Livros from "./Livros";
import { useState } from "react";
interface Livro{
    codigo: number; 
    codEditora: number; 
    titulo: string;
    resumo: string;
    autores: string[];
}

class ControleLivro {
    lista_livros: Livro[];

    constructor(lista_livros: Livro[]) {
        this.lista_livros = lista_livros;
    }

    obterLivros(): Livro[] {
        return this.lista_livros.filter(livro => livro.codigo >= 0);
    }

    incluirLivro(lista: Livro[]): void { 
        console.log("x: ", this.lista_livros.length);
        const tam_lista: number = this.lista_livros.length; 
        if (tam_lista >= 0){
            lista[0].codigo = tam_lista + 1; 
        }else{
            lista[0].codigo = 1;
        }

        this.lista_livros = this.lista_livros.concat(lista);

    }
    excluir(codigo: number): void{
        const index: number = lista_livros.findIndex(lista_livros => lista_livros.codigo === codigo); 
        lista_livros.splice(index, 1);

    }

}


const [lista_livros, setListaLivros] = useState([
    {codigo: 1, codEditora: 1, titulo: "Livro A", resumo: "Abecedário", autores:["Marcos", "Julio"]},
    {codigo: 2, codEditora: 2, titulo: "Livro C", resumo: "Abecedário C", autores:["Fatima", "Antonio"]},
    {codigo: 3, codEditora: 3, titulo: "Livro B", resumo: "Abecedário B", autores:["Juliana", "Joao"]},
]);
const novoLivro: Livro[] = [{codigo: 1, codEditora: 1, titulo: "Livro D", resumo: "Abecedário D", autores:["José", "Mario"]}];
const test_classe_livros = new ControleLivro(lista_livros);
test_classe_livros.incluirLivro(novoLivro);
