import React, { useState, useEffect } from 'react';
import { ControleLivro, lista_livros } from './controle/ControleLivros';
import { ControleEditora, editoras } from './controle/ControleEditora';

function LivroLista(props) {
    const controleLivro = new ControleLivro(lista_livros);
    const controleEditora = new ControleEditora(editoras); 
    const listaLivros = controleLivro.obterLivros();
    const LinhaLivro = (props) => {
        const { livro } = props;

        // Obter o nome da editora
        const nomeEditora = controleEditora.getNomeEditora(livro.codEditora);

        return (
            <table>
                <tr>
                    <th> Titulo</th>
                    <th> Resumo</th>
                    <th> Editora</th>
                    <th> Autores</th>
                </tr>
                <tr>

                    <td>
                        <td>{livro.titulo}</td>
                        <button onClick={() => controleLivro.excluir(livro.codigo)}>Excluir</button>
                    </td>

                    <td>{livro.resumo}</td>
                    <td>
                        <ul>
                            {livro.autores.map((autor, index) => (
                                <li key={index}>{autor}</li>
                            ))}
                        </ul>
                    </td>
                    <td>{livro.editora}</td>
                </tr>
            </table>
        )
    };

    return (
        <div>
            {listaLivros.map((livro, index) => (<LinhaLivro livro={livro}/>))}
        </div>
  );
}


export default LivroLista;
