import Livros from "./Livros";
interface Livro{
    codigo: number; 
    codEditora: number; 
    titulo: string;
    resumo: string;
    autores: string[];
}

class ControleLivro {
    lista_livros: Livro[];

    constructor(lista_livros: Livro[]) {
        this.lista_livros = lista_livros;
    }

    obterLivros(): Livro[] {
        return this.lista_livros.filter(livro => livro.codigo >= 0);
    }

    incluirLivro(lista: Livro[]): void { 
        console.log("x: ", this.lista_livros.length);
        const tam_lista: number = this.lista_livros.length; 
        if (tam_lista >= 0){
            lista[0].codigo = tam_lista + 1; 
        }else{
            lista[0].codigo = 1;
        }

        this.lista_livros = this.lista_livros.concat(lista);

    }
    excluir(codigo: number): void{
        const index: number = lista_livros.findIndex(lista_livros => lista_livros.codigo === codigo); 
        lista_livros.splice(index, 1);
    }

}



