import Livros from "./Livros";
interface Livro{
    codigo: number; 
    codEditora: number; 
    titulo: string;
    resumo: string;
    autores: string[];
}

class ControleLivro{
    lista_livros: Livro[];
    codigo: number;
    codEditora: number;
    titulo: string;
    resumo: string;
    autores: string[];


    constructor(lista_livros: Livro[], codigo: number, codEditora: number, titulo: string, resumo: string, autores:string[]){
        this.lista_livros = lista_livros;
        this.codigo = codigo;
        this.codEditora = codEditora;
        this.titulo = titulo;
        this.resumo = resumo;
        this.autores = autores;
    }


    obterLivros(): Livro[]{
        return lista_livros.filter(lista_livros => lista_livros.codigo >= 0);
    }


    incluirLivro(codigo: number, codEditora: number, titulo: string, resumo: string, autores:string[]): void{
        lista_livros.fill(this.codigo = codigo, this.codEditora = codEditora, this.titulo = titulo, this.resumo = resumo, this.autores = autores);

    }

}

/*
const editoras: Editora[] = [
    {codEditora: 1, nome: "Editora A"}, 
    {codEditora: 2, nome: "Editora C"},
    {codEditora: 3, nome: "Editora B"},
  ];
*/
const lista_livros: Livro[] = [
    {codigo: 1, codEditora: 1, titulo: "Livro A", resumo: "Abecedário", autores:["Marcos", "Julio"]},
    {codigo: 2, codEditora: 2, titulo: "Livro C", resumo: "Abecedário C", autores:["Fatima", "Antonio"]},
    {codigo: 3, codEditora: 3, titulo: "Livro B", resumo: "Abecedário B", autores:["Juliana", "Joao"]},
];

const test_classe_livros = new ControleLivro(lista_livros);
console.log("Esses são os livros: ",  test_classe_livros.obterLivros())

const livros_array: string = JSON.stringify(lista_livros);


export {};
